import axios from "axios";

export default function() {
    const [total, setTotal] = React.useState({});

    const SetT = async () => {
        const rec = await axios({
            "method":"GET",
            "url":"https://corona.lmao.ninja/all"
            })
        .then((response)=>{
            const Timeline = response.data;
            return Timeline;
        })
        .catch((error)=>{
            console.log(error)
    });
    setTotal(rec);
   }

    SetT();
    return total;
}
