import React, { useState } from "react";
import {bubble as Menu} from "react-burger-menu";

function Men(props) {

    const [check, setCheck] = useState(false);

    function checkbox() {
      if(check)
        setCheck(false);
      else
        setCheck(true);
    }

    return (

      <Menu>
        <a onClick={() => {
              props.toShow(1);
              setCheck(false);
            }} href="#">World</a>
        <a> onClick={() => {
              setCheck(false);
              props.toShow(2);
            }}>India</a>
      </Menu>
    );
