import React, { useState } from "react";
import {connect} from 'react-redux'

function World (props) {

    const [total, setTotal] = useState();
    const n = props.total.then((data) => {setTotal(data)});
    console.log(total);
       

    return (
        <div className="total">
            <ul>
                <li className="orange">
                    <img src="./Images/icon-infected.png" alt="infected"></img>
                    <strong>{props.total.cases}</strong>
                    <span>Total Cases</span>
                </li>
                <li className="green">
                    <img src="./Images/icon-inactive.png" alt="safe"></img>
                    <strong>{props.total.recovered}</strong>
                    <span>Cured/Dischrged</span>
                </li>
                <li className="red">
                    <img src="./Images/icon-death.png" alt="dead"></img>
                    <strong>{props.total.deaths}</strong>
                    <span>Deaths</span>
                </li>
                <li className="blue">
                    <img src="./Images/icon-active.png" alt="active"></img>
                    <strong>{props.total.active}</strong>
                    <span>Active Cases</span>
                </li>
            </ul>
        </div>
    );
}


function mapStateToProps(state) {
    return {
        total: state.worldTotal
    };
  }
  
  
  export default connect(mapStateToProps)(World);
