import React, { useContext } from "react";
import Paper from "@material-ui/core/Paper";
import {HistoryContext} from '../Store';
import axios from "axios";
import {
  Chart,
  Series,
  ArgumentAxis,
  CommonSeriesSettings,
  Export,
  Legend,
  Margin,
  Title,
  Subtitle,
  Tooltip,
  Label,
  Grid
} from "devextreme-react/chart";


function Graph(props) {

  const [history, setHistory] = useContext(HistoryContext);

  const Hist = async () => {
    const rec = await axios({
    "method": "get",
    "url": "https://corona.lmao.ninja/v2/historical"})
    .then((response)=>{
        const Timeline = response.data;
        return Timeline;
    })
    .catch((error)=>{
        console.log(error)
  });
  setHistory(rec)
  }

  if(history.length===0)
    Hist();

  const info = history.filter((rec)=>(rec.country===props.name))

  function getData(record) {

    const Cases = record.timeline.cases;
    const Deaths = record.timeline.deaths;
    const Recovered = record.timeline.recovered;
    const data = []
    const dates = Object.keys(Cases);
    var date;
    for(date of dates) {
       if(Cases[date]>0){
          if(date.length===7)
            data.push({argument:date.substring(0,4) , cases: Cases[date], deaths: Deaths[date], recovered: Recovered[date] });
          else
            data.push({argument:date.substring(0,3) , cases: Cases[date], deaths: Deaths[date], recovered: Recovered[date] });       
          }
    }

    return (
      <Chart
          palette="Violet"
          dataSource={data}
        >
          <CommonSeriesSettings
            argumentField="argument"
            type="line"
          />
          <Series key="cases" valueField="cases" name="Total Cases" />
          <Series key="deaths" valueField="deaths" name="Total Deaths" />
          <Series key="recovered" valueField="recovered" name="Total Recoveries" />
          <Margin bottom={20} right={10} />
          <ArgumentAxis
            valueMarginsEnabled={false}
            discreteAxisDivisionMode="crossLabels"
          >
          <Label
              wordWrap="none"
              overlappingBehavior={"hidden"}
            />
          <Grid visible={true} />
          </ArgumentAxis>
          <Legend
              verticalAlignment="bottom"
              horizontalAlignment="center"
              itemTextPosition="bottom"
            />
            <Title text={"Corona Record in " + props.name}>
              
            </Title>
            <Tooltip enabled={true} />
          </Chart>
    )
    
  }

  return (
    <Paper>

      {info.map(getData)}
      <div className="option">
        <SelectBox
          dataSource={types}
          value={this.state.type}
          onValueChanged={this.handleChange}
        />
      </div>
      
    </Paper>
  );
}




export default (Graph);


