import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import axios from "axios";

const useStyles = makeStyles({
  table: {
    minWidth: 650,
    maxWidth: 500,
    background: "black"
  }
});


export default function SimpleTable() {
    const classes = useStyles();
    const [records, setRecords] = useState([]);

    function SetR() {
        axios({
            "method":"GET",
            "url":"https://corona.lmao.ninja/countries",
            "params":{
            "sort":"deaths"
            }
            })
        .then((response)=>{
            const Timeline = response.data;
            setRecords(Timeline);
        })
        .catch((error)=>{
            console.log(error)
    });
   }
    setInterval(SetR, 1000);

    function Display(record) {
        console.log(record);
        return(
            <TableRow key={record.country}>
              <TableCell component="th" scope="row">
                {record.country}
              </TableCell>
              <TableCell align="right">{record.cases}</TableCell>
              <TableCell align="right">{record.deaths}</TableCell>
              <TableCell align="right">{record.recovered}</TableCell>
            </TableRow>
          );
    }


  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Country</TableCell>
            <TableCell align="right">Cases&nbsp;</TableCell>
            <TableCell align="right">Deaths&nbsp;</TableCell>
            <TableCell align="right">Recoveries&nbsp;</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {records.map(Display)}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
