import React, {useState} from "react";

export const IndiaContext = React.createContext(null);
export const IndiaFullContext = React.createContext(null);
export const WorldContext = React.createContext(null);
export const WorldFullContext = React.createContext(null);
export const HistoryContext = React.createContext(null);

const Store = ({children}) => {
    const [ind, setInd] = useState(null);
    const [indTotal, setIndTotal] = useState(null);
    const [world, setWorld] = useState(null);
    const [wTotal, setWorldtotal] = useState(null);
    const [history, setHistory] = useState(null);
    
    return(
        <WorldContext.Provider value={[world, setWorld]}>
            <WorldFullContext.Provider value={[wTotal, setWorldtotal]}>
                <HistoryContext.Provider value={[history, setHistory]}>
                    <IndiaContext.Provider value={[ind, setInd]}>
                        <IndiaFullContext.Provider value={[indTotal, setIndTotal]}>
                            {children}
                        </IndiaFullContext.Provider>                        
                    </IndiaContext.Provider>
                </HistoryContext.Provider>
            </WorldFullContext.Provider>
        </WorldContext.Provider>
    )
} 

export default Store;
