  return (
    <Paper>
      <Chart data={data} width={350} height={350}>
        <ArgumentAxis showGrid />
        <ValueAxis />

        <SplineSeries valueField="value" argumentField="argument" />
      </Chart>
    </Paper>
  );
}

export default Graph;
