import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import axios from "axios";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <Typography
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`vertical-tabpanel-${index}`}
      aria-labelledby={`vertical-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={8
      
      }>{children}</Box>}
    </Typography>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `vertical-tab-${index}`,
    'aria-controls': `vertical-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 6,
    backgroundColor: theme.palette.background.paper,
    display: 'flex',
    height: 500,
    position:"absolute",
    top:200,
    left:500,
    width:800,
    borderRadius: 10,
    maxWidth: 960,
    margin: "auto",
    zIndex: -1,
  },
  tabs: {
    borderRight: `1px solid ${theme.palette.divider}`,
  },
}));

export default function VerticalTabs() {
  const classes = useStyles();
  const [value, setValue] = useState(0);
  const [records, setRecords] = useState([]);

    function SetR() {
        axios({
            "method":"GET",
            "url":"https://corona.lmao.ninja/countries",
            "params":{
            "sort":"deaths"
            }
            })
        .then((response)=>{
            const Timeline = response.data;
            setRecords(Timeline);
        })
        .catch((error)=>{
            console.log(error)
    });
   }
    SetR();
    setInterval(SetR, 1000000);

    function setLabel(record, index){
        return (
            <Tab label={record.country} {...a11yProps(index)} />
        );
    }

    function setPanel(record, index) {
        return(
            <TabPanel value={value} index={index}>
                <b>{record.country}</b><br/><br/>
                Total Cases:{record.cases}<br/>
                Deaths:{record.deaths}<br/>
                Recoveries{record.recovered}
            </TabPanel>
        )
    }

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <Tabs
        orientation="vertical"
        variant="scrollable"
        value={value}
        onChange={handleChange}
        aria-label="Corona Live"
        className={classes.tabs}
      >
        {records.map(setLabel)}
      </Tabs>
      {records.map(setPanel)}
    </div>
  );
}
