import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import axios from "axios";
import Graph from "./Graph";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <Typography
      component="div"
      role="tabpanel"
      hidden={value !== index}
      id={`vertical-tabpanel-${index}`}
      aria-labelledby={`vertical-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={3}>{children}</Box>}
    </Typography>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `vertical-tab-${index}`,
    'aria-controls': `vertical-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 3,
    backgroundColor: theme.palette.background.paper,
    display: 'flex',
    height: 500,
    position:"absolute",
    top:200,
    left:300,
    width:1000,
    borderRadius: 10,
    maxWidth: 960,
    margin: "auto",
    zIndex: -1,
  },
  tabs: {
    borderRight: `1px solid ${theme.palette.divider}`,
  },
}));

export default function VerticalTabs() {
  const classes = useStyles();
  const [value, setValue] = useState(0);
  const [records, setRecords] = useState([]);
  const [history, setHist] = useState([]);

    const SetR = async () => {
        const rec = await axios({
            "method":"GET",
            "url":"https://corona.lmao.ninja/countries",
            "params":{
            "sort":"cases"
            }
            })
        .then((response)=>{
            const Timeline = response.data;
            return Timeline;
        })
        .catch((error)=>{
            console.log(error)
    });
    setRecords(rec);
   }

   const SetH = async () => {
      const rec = await axios("https://corona.lmao.ninja/v2/historical")
      .then((response)=>{
          const Timeline = response.data;
          return Timeline;
      })
      .catch((error)=>{
          console.log(error)
  });
  setHist(rec);
  }



    SetR();
    SetH();
    setInterval(SetR, 1000000);
    setInterval(SetH,86400000)

    function setLabel(record, index){
        return (
            <Tab label={record.country} {...a11yProps(index)} />
        );
    }

    function setPanel(record, index) {

        const data = history.filter((rec)=>(rec.country===record.country));
        console.log(data);
        
    
        return(
            <TabPanel value={value} index={index}>
            <div className = "countryDetail">
                <h1> {record.country}</h1><br/>
                <p>Total Cases: {record.cases}</p>
                <p>New Cases Today: {record.todayCases}</p>
                <p><p className="red">Deaths: {record.deaths}</p></p>
                <p>New Deaths Today: {record.todayDeaths}</p>
                <p><p className="green">Recoveries: {record.recovered}</p></p>
                <p>Active Cases: {record.critical}</p>
                <p>Critical Cases: {record.critical}</p>
            </div>
            <div className="graph"><Graph data={data} ></Graph></div>
            </TabPanel>
        )
    }

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <Tabs
        orientation="vertical"
        variant="scrollable"
        value={value}
        onChange={handleChange}
        aria-label="Corona Live"
        className={classes.tabs}
      >
        {records.map(setLabel)}
      </Tabs>
      {records.map(setPanel)}
    </div>
  );
}
